import React from 'react';
import './App.css';
import firebase from "./Firebase";
import a from "./img.png";
import GoogleMapReact from 'google-map-react';
const AnyReactComponent = ({ text }) => <img src={a} height={"30px"} width={"30px"}/>;


class App extends React.Component{
  state = {
    center: {
      lat: 27.714750,
      lng: 85.309285
    },
    zoom: 15,
    status: true,
    bus:{}
  };

  componentDidMount(){
    console.log(navigator.geolocation.watchPosition(this.showPosition))
    const busRef = firebase.database().ref('online_drivers');
    busRef.on('value', (snapshot)=>{
      let bus = snapshot.val();
      let newState = {};
      console.log(bus);
    })
  }

  showPosition = (position) => {
    console.log(position)
    this.setState({
      center:{
        lat: position.coords.latitude,
        lng: position.coords.longitude
      },
      status : true

    })

    console.log(this.state.center)
  }

  render(){
  return (
    <div style={{ height: '100vh', width: '100%' }}>
      {this.state.status?
    <GoogleMapReact
      bootstrapURLKeys={{ key: "AIzaSyBLFv4JVBlC3pXdvO7TBUBVWgrEq4bb_y0" }}
      defaultCenter={this.state.center}
      defaultZoom={this.state.zoom}
    >
      {console.log(this.state.center)}
      <AnyReactComponent
        lat={this.state.center.lat}
        lng={this.state.center.lng}
        text="My Marker"
      />
    </GoogleMapReact>
    : ""}
  </div>
  );
  }
}

export default App;
